import React from 'react';
import { View, Text, TextInput, TouchableOpacity, Image, StyleSheet } from 'react-native';

const PlusImg = require('./assets/plus.png'); // 추가할 이미지 경로
const CloudImg = require('./assets/cloud.png'); // 추가할 이미지 경로
const BlackCloudImg = require('./assets/black_cloud.png'); // 추가할 이미지 경로
const DeleteImg = require('./assets/delete.png'); // 추가할 이미지 경로

const GoalComponent = ({
  goalId,
  goalTitles,
  goalContents,
  isEditingTitle,
  updateGoalTitle,
  handleTitleKeyDown,
  addContentToGoal,
  toggleContentCompletion,
  updateContentText,
  handleContentKeyDown,
  deleteContent,
  setIsEditingTitle,
  setGoalContents,
}) => {
  return (
    <View key={goalId}>
      <View style={styles.goalHeader}>
        {isEditingTitle[goalId] ? (
          <TextInput
            style={styles.goalTitleInput}
            value={goalTitles[goalId]}
            onChangeText={(text) => updateGoalTitle(goalId, text)}
            onSubmitEditing={(e) => handleTitleKeyDown(e, goalId)}
          />
        ) : (
          <Text
            style={styles.goalTitle}
            onPress={() => setIsEditingTitle({ ...isEditingTitle, [goalId]: true })}
          >
            {goalTitles[goalId]}
          </Text>
        )}
        <TouchableOpacity style={styles.addButton} onPress={() => addContentToGoal(goalId)}>
          <Image source={PlusImg} style={styles.plusImg} />
        </TouchableOpacity>
      </View>

      {goalContents[goalId]?.map((content, index) => (
        <View key={index} style={[styles.todoItem, content.completed && styles.completed]}>
          <TouchableOpacity style={styles.completeButton} onPress={() => toggleContentCompletion(goalId, index)}>
            <Image
              source={content.completed ? CloudImg : BlackCloudImg}
              style={styles.cloudImg}
            />
          </TouchableOpacity>
          {content.isEditing ? (
            <TextInput
              style={styles.todoInput}
              value={content.text}
              onChangeText={(text) => updateContentText(goalId, index, text)}
              onSubmitEditing={(e) => handleContentKeyDown(e, goalId, index)}
            />
          ) : (
            <Text
              style={styles.todoText}
              onPress={() => {
                setGoalContents({
                  ...goalContents,
                  [goalId]: goalContents[goalId].map((content, i) => i === index ? { ...content, isEditing: true } : content)
                });
              }}
            >
              {content.text}
            </Text>
          )}
          <TouchableOpacity style={styles.deleteButton} onPress={() => deleteContent(goalId, index)}>
            <Image source={DeleteImg} style={styles.deleteImg} />
          </TouchableOpacity>
        </View>
      ))}
    </View>
  );
};

const styles = StyleSheet.create({
  app: {
    textAlign: 'center',
    width: '100%',
    height: '100%',
    backgroundColor: '#FFFBF1',
    padding: 20,
  },
  titleImg: {
    top: 10,
    left: 50,
    width: 170,
    height: 150,
  },
  appTitle: {
    fontSize: 30,
    color: '#333',
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 20,
    marginTop: 30,
  },
  appContainer: {
    flex: 1,
    justifyContent: Platform.OS === 'web' ? 'flex-start' : 'center',
    alignItems: Platform.OS === 'web' ? 'flex-start' : 'center',
    marginHorizontal: 40,
    flexDirection: Platform.OS === 'web' ? 'row' : 'column',
  },
  calendarContainer: {
    width: Platform.OS === 'web' ? '40%' : '100%',
    padding: 10,
    backgroundColor: '#FFFBF1',
  },
  dateText: {
    fontSize: 18,
  },
  todoContainer: {
    width: Platform.OS === 'web' ? '60%' : '100%',
    textAlign: 'left',
    padding: 10,
    borderRadius: 10,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    marginTop: Platform.OS === 'web' ? 0 : 20,
    backgroundColor: 'transparent',
  },
  todoTitle: {
    fontSize: 18,
    color: '#333',
    marginBottom: 20,
  },
  goalHeader: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#D9D9D9',
    borderRadius: 20,
    padding: 10,
    minWidth: '30%',
    maxWidth: '90%',
    flexGrow: 1,
    flexShrink: 1,
  },
  goalTitle: {
    flex: 1,
    fontSize: 18,
    fontWeight: 'bold',
    flexGrow: 1,
    flexShrink: 1,
  },
  goalTitleInput: {
    flex: 1,
    padding: 10,
    fontSize: 18,
    backgroundColor: '#D9D9D9',
    borderRadius: 20,
    borderWidth: 1,
    borderColor: '#ddd',
    flexGrow: 1,
    flexShrink: 1,
  },
  addGoalButton: {
    backgroundColor: 'skyblue',
    padding: 10,
    alignItems: 'center',
    marginTop: 10,
  },
  addGoalButtonText: {
    color: 'white',
    fontWeight: 'bold',
  },
  todoInputContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 20,
  },
  addTodoTitle: {
    flex: 1,
    padding: 10,
    fontSize: 16,
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 5,
    marginRight: 10,
  },
  addButton: {
    padding: 10,
    borderRadius: 5,
  },
  plusImg: {
    width: 40,
    height: 40,
  },
  todoList: {
    padding: 0,
  },
  todoItem: {
    padding: 10,
    marginVertical: 10,
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    borderBottomWidth: 2,
    borderBottomColor: 'black',
    fontWeight: 'bold',
  },
  contentTextInput: {
    flex: 1,
    padding: 10,
    fontSize: 16,
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 5,
    marginRight: 10,
    width: '70%', // Adjust width as needed
  },
  completed: {
    textDecorationLine: 'line-through',
  },
  todoText: {
    flex: 1,
    marginRight: 10,
  },
  completeButton: {
    padding: 5,
    borderRadius: 5,
  },
  cloudImg: {
    width: 40,
    height: 30,
  },
  deleteButton: {
    padding: 5,
    borderRadius: 5,
  },
  deleteImg: {
    width: 10,
    height: 10,
    marginRight: 10,
  },
  todoInput: {
    flex: 1,
    padding: 10,
    fontSize: 16,
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 5,
    marginRight: 10,
  },
  todoTitleText: {
    flex: 1,
    padding: 10,
    fontSize: 16,
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 5,
    marginRight: 10,
  },
});

export default GoalComponent;
