import { StyleSheet, Platform } from 'react-native';

const styles = StyleSheet.create({
  app: {
    // 앱의 기본 스타일 설정
    textAlign: 'center', 
    width: '100%',
    height: '100%',
    backgroundColor: '#FFFBF1',
    padding: 20,
  },
  titleImg: {
    // 제목 이미지 스타일
    top: 10,
    left: 50,
    width: 170,
    height: 150,
  },
  appTitle: {
    // 앱 제목 텍스트 스타일
    fontSize: 30,
    color: '#333',
    fontWeight: 'bold',
    textAlign: 'center',
    marginBottom: 20,
    marginTop: 30,
  },
  appContainer: {
    // 앱 컨테이너 스타일 (플랫폼에 따라 다르게 설정)
    flex: 1,
    justifyContent: Platform.OS === 'web' ? 'flex-start' : 'center',
    alignItems: Platform.OS === 'web' ? 'flex-start' : 'center',
    marginHorizontal: 40,
    flexDirection: Platform.OS === 'web' ? 'row' : 'column',
  },
  calendarContainer: {
    // 캘린더 컨테이너 스타일
    width: Platform.OS === 'web' ? '40%' : '100%',
    padding: 10,
    backgroundColor: '#FFFBF1',
  },
  dateText: {
    // 날짜 텍스트 스타일
    fontSize: 18,
  },
  todoContainer: {
    // 할 일 컨테이너 스타일
    width: Platform.OS === 'web' ? '60%' : '100%',
    textAlign: 'left',
    padding: 10,
    borderRadius: 10,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    marginTop: Platform.OS === 'web' ? 0 : 20,
    backgroundColor: 'transparent',
  },
  todoTitle: {
    // 할 일 제목 스타일
    fontSize: 18,
    color: '#333',
    marginBottom: 20,
  },
  goalHeader: {
    // 목표 헤더 스타일
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#D9D9D9',
    width: '30%',
    borderRadius: 20,
  },
  goalTitle: {
    // 목표 제목 텍스트 스타일
    flex: 1, 
    alignItems: 'center',
    padding: 10,
    fontSize: 18, 
    fontWeight: 'bold',
    marginLeft: 20,
  },
  goalTitleInput: {
    // 목표 제목 입력 스타일
    flex: 1, 
    padding: 10,
    fontSize: 18,
    alignItems: 'center',
    backgroundColor: '#D9D9D9',
    borderRadius: 20,
    borderWidth: 1,
    width: '30%',
    borderColor: '#ddd',
    marginLeft: 20,
  },
  addGoalButton: {
    // 목표 추가 버튼 스타일
    backgroundColor: 'skyblue',
    padding: 10,
    alignItems: 'center',
    marginTop: 10,
  },
  addGoalButtonText: {
    // 목표 추가 버튼 텍스트 스타일
    color: 'white',
    fontWeight: 'bold',
  },
  todoInputContainer: {
    // 할 일 입력 컨테이너 스타일
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 20,
  },
  addTodoTitle: {
    // 할 일 제목 추가 입력 스타일
    flex: 1,
    padding: 10,
    fontSize: 16,
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 5,
    marginRight: 10,
  },
  addButton: {
    // 추가 버튼 스타일
    padding: 10,
    borderRadius: 5,
  },
  plusImg: {
    // 플러스 이미지 스타일
    width: 40,
    height: 40,
  },
  todoList: {
    // 할 일 목록 스타일
    padding: 0,
  },
  todoItem: {
    // 할 일 항목 스타일
    padding: 10,
    marginVertical: 10,
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    borderBottomWidth: 2,
    borderBottomColor: 'black',
    fontWeight: 'bold',
  },
  contentTextInput: {
    // 할 일 내용 입력 스타일
    flex: 1,
    padding: 10,
    fontSize: 16,
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 5,
    marginRight: 10,
    width: '70%', // 필요에 따라 조정
  },
  completed: {
    // 완료된 할 일 스타일
    textDecorationLine: 'line-through',
  },
  todoText: {
    // 할 일 텍스트 스타일
    flex: 1,
    marginRight: 10,
  },
  completeButton: {
    // 완료 버튼 스타일
    padding: 5,
    borderRadius: 5,
  },
  cloudImg: {
    // 구름 이미지 스타일
    width: 40,
    height: 30,
  },
  deleteButton: {
    // 삭제 버튼 스타일
    padding: 5,
    borderRadius: 5,
  },
  deleteImg: {
    // 삭제 이미지 스타일
    width: 10,
    height: 10,
    marginRight: 10,
  },
  todoInput: {
    // 할 일 입력 스타일
    flex: 1,
    padding: 10,
    fontSize: 16,
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 5,
    marginRight: 10,
  },
  todoTitleText: {
    // 할 일 제목 텍스트 스타일
    flex: 1,
    padding: 10,
    fontSize: 16,
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 5,
    marginRight: 10,
  },
});

export default styles;
